import './App.css';
import React from 'react';
import { animate, motion } from "framer-motion"; 
import { BsFillArrowLeftCircleFill } from "react-icons/bs";
import { useState } from "react";




function Nav() {
  return (
    <div className="right-sidebar">
      <ul>
        <li>Item 1</li>
        <li>Item 2</li>
        <li>Item 3</li>
      </ul>
    </div>
  );
}


function App() {

  let [isOpen, setIsOpen] = useState(false);

  return (


    <div className='nav'>
        <motion.div animate={{x:-500}} className="box"> </motion.div>
        <motion.btn layout 
        data-isOpen={isOpen}

        class="menubtn" onClick={() => setIsOpen(!isOpen)}>

          <BsFillArrowLeftCircleFill className='menuicon'/>

        </motion.btn>
    </div>
  );
}


export default App;
