export function buttonon(){
    
    function buttonon(){
        const button = document.getElementById("menubtn")
        const navb = document.getElementById("navb")
        const iconElement = document.getElementById('icon');
              let pressed = false;
      
              button.addEventListener("click", function() {
                  pressed = !pressed;
                  if (pressed) {
                      
                      button.style.transitionDelay="0ms"
                      button.classList.add("pressed");
                      document.getElementById("colum1").style.height = "120%";
                      document.getElementById("colum1").style.transitionDelay = "0ms";
                    
                      document.getElementById("colum2").style.height = "120%";
                      document.getElementById("colum2").style.transitionDelay = "200ms";
                    
                      document.getElementById("colum3").style.height = "120%";
                      document.getElementById("colum3").style.transitionDelay = "400ms";
                      iconElement.classList.remove('bi-check-circle');
                      iconElement.classList.add('bi-x-circle');
                      setTimeout(() => {
                        document.getElementById("colum2").style.transitionDelay = "0ms";
                        document.getElementById("colum3").style.transitionDelay = "0ms";
                      }, 400);
      
                      
                  } else {
                     
                      document.getElementById("colum1").style.height = "0%";
                      
                    
                      document.getElementById("colum2").style.height = "0%";
                      document.getElementById("colum2").style.transitionDelay = "0ms";
                    
                      document.getElementById("colum3").style.height = "0%";
                      document.getElementById("colum3").style.transitionDelay = "0ms";
                      setTimeout(() => {
                        button.classList.remove("pressed");
                        iconElement.classList.remove('bi-x-circle');
                        iconElement.classList.add('bi-check-circle');
                      }, 400);
                      
                  }
              });
      }
      
}
