import './App.css';
import { motion } from "framer-motion"
import { BsFillArrowLeftCircleFill } from "react-icons/bs";
import React, { useState } from 'react';

function Nav() {
  return (
    <div className="right-sidebar">
      <ul>
        <li>Item 1</li>
        <li>Item 2</li>
        <li>Item 3</li>
      </ul>
    </div>
  );
}

function App() {
  const [showNav, setShowNav] = useState(false);

  const toggleNav = () => {
    setShowNav(!showNav);
  };

  return (
    <div className='app'>
      <motion.button
        animate={{ x: 100 }}
        className="menubtn"
        onClick={toggleNav}
      >
        <BsFillArrowLeftCircleFill className='menuicon'/>
      </motion.button>
      {showNav && <Nav />}
    </div>
  );
}

export default App;
